/**
  * @brief  The application entry point.
  * @retval int
  */
int main(void)
{
  /* USER CODE BEGIN 1 */

  /* USER CODE END 1 */

  /* MCU Configuration--------------------------------------------------------*/

  /* Reset of all peripherals, Initializes the Flash interface and the Systick. */
  HAL_Init();

  /* USER CODE BEGIN Init */

  /* USER CODE END Init */

  /* Configure the system clock */
  SystemClock_Config();

  /* USER CODE BEGIN SysInit */

  /* USER CODE END SysInit */

  /* Initialize all configured peripherals */
  MX_GPIO_Init();
  MX_USART2_UART_Init();
  /* USER CODE BEGIN 2 */

  /* USER CODE END 2 */

  /* Infinite loop */
  /* USER CODE BEGIN WHILE */
  while (1)
  {
	  CS_now = HAL_GPIO_ReadPin(GPIOB, CS);
	  if (CS_now == 0 && CS_old == 1){
	    write_miso(response[0]);
	    while (1){
	      SCLK_now = HAL_GPIO_ReadPin(GPIOA, SCLK);
	      CS_now = HAL_GPIO_ReadPin(GPIOB, CS);
	      if(SCLK_now == 0 && SCLK_old == 1){
	        write_miso(response[bit_index]);
	      }
	      if(SCLK_now == 1 && SCLK_old == 0){
	        value = HAL_GPIO_ReadPin(GPIOA, MOSI);
	        data[bit_index] = value;
	        //write_miso(response[bit_index]);
	        bit_index ++;
	      }
	      if (CS_now == 1 && CS_old == 0){
	        if(response[31] == 1){
	          write_miso(0);
	        }
	        break;
	      }
	      SCLK_old = SCLK_now;
	      CS_old = CS_now;
	    }
	    // Mode 001:
	    if(data[3] && !data[2] && !data[1]){
	      for(int i=0; i<32; i=i+1){
	        response[i] = 0;
	      }
	      test = 1;
	    }
	    if(test){
	      int_2_array(volt_measure[measure],curr_measure[measure],0);
	      measure++;
	    }
	  }
	  bit_index = 0;
	  CS_old = CS_now;
    /* USER CODE END WHILE */

    /* USER CODE BEGIN 3 */
  }
  /* USER CODE END 3 */
}

